package demo.cag

import java.awt.*
import java.awt.geom.Arc2D
import java.awt.geom.Ellipse2D
import java.awt.geom.Line2D
import java.awt.geom.Path2D
import java.awt.image.BufferedImage
import java.awt.image.BufferedImage.TYPE_INT_ARGB
import java.io.ByteArrayInputStream
import java.util.*
import javax.imageio.ImageIO
import kotlin.math.cos
import kotlin.math.sin

object Icons {

    fun edit(color: Color = Color.BLACK) =
            withBase64("", color)

    fun union(color: Color = Color.BLACK) =
            withBase64("", color)

    fun difference(color: Color = Color.BLACK) =
            withBase64("", color)

    fun intersect(color: Color = Color.BLACK) =
            withBase64("", color)

    fun delete(color: Color = Color.BLACK) =
            withBase64("", color)

    fun triangle(color: Color = Color.BLACK): BufferedImage {
        val shape = Path2D.Double()
        shape.moveTo(16.0, 3.0)
        shape.lineTo(29.0, 27.0)
        shape.lineTo(3.0, 27.0)
        shape.closePath()
        return withShape(shape, color)
    }

    fun square(color: Color = Color.BLACK): BufferedImage {
        val shape = Path2D.Double()
        shape.moveTo(3.0, 3.0)
        shape.lineTo(27.0, 3.0)
        shape.lineTo(27.0, 27.0)
        shape.lineTo(3.0, 27.0)
        shape.closePath()
        return withShape(shape, color)
    }

    fun rectangle(color: Color = Color.BLACK): BufferedImage {
        val shape = Path2D.Double()
        shape.moveTo(6.0, 2.0)
        shape.lineTo(25.0, 2.0)
        shape.lineTo(25.0, 29.0)
        shape.lineTo(6.0, 29.0)
        shape.closePath()
        return withShape(shape, color)
    }

    fun pentagon(color: Color = Color.BLACK): BufferedImage {
        val radius = 14.5
        val shape = Path2D.Double()
        shape.moveTo(16.0 + radius * cos(0.0), 16.0 + radius * sin(0.0))
        for (i in 1..4) {
            val arc = i * 2.0 * Math.PI / 5.0
            val x = 16.0 + radius * cos(arc)
            val y = 16.0 + radius * sin(arc)
            shape.lineTo(x, y)
        }
        shape.closePath()
        return withShape(shape, color)
    }

    fun circle(color: Color = Color.BLACK) = withShape(Ellipse2D.Double(2.0, 2.0, 27.0, 27.0), color)

    fun capsule(color: Color = Color.BLACK): BufferedImage {
        val shape = Path2D.Double()
        shape.append(Arc2D.Double(7.0, 2.0, 17.0, 17.0, 0.0, 180.5, Arc2D.OPEN), false)
        shape.append(Arc2D.Double(7.0, 12.0, 17.0, 17.0, 180.0, 180.0, Arc2D.OPEN), true)
        shape.closePath()
        return withShape(shape, color)
    }

    fun error(): BufferedImage {
        val shape = Path2D.Double()
        shape.append(Line2D.Double(3.0, 3.0, 29.0, 29.0), false)
        shape.append(Line2D.Double(29.0, 3.0, 3.0, 29.0), false)
        return withShape(shape, Color.RED)
    }

    private fun withBase64(encoded: String, color: Color = Color.BLACK): BufferedImage {
        val decoded = Base64.getDecoder().decode(encoded)
        ByteArrayInputStream(decoded).use { return blend(ImageIO.read(it), color.rgb) }
    }

    private fun withShape(shape: Shape, color: Color): BufferedImage {
        val img = createIconImage()
        val g = configureGraphics(img)
        g.color = color
        g.draw(shape)
        return img
    }

    private fun createIconImage() = BufferedImage(32, 32, TYPE_INT_ARGB)

    private fun configureGraphics(source: BufferedImage): Graphics2D {
        val g = source.graphics as Graphics2D
        g.stroke = BasicStroke(1.8f)
        g.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON)
        return g
    }

    private fun blend(image: BufferedImage, color: Int): BufferedImage {
        val blend = color.and(0xFFFFFF)
        for (y in 0 until image.height) {
            for (x in 0 until image.width) {
                image.setRGB(x, y, image.getRGB(x, y) or blend)
            }
        }
        return image
    }
}
